function sendRandomNumber() {
    // Generar un número aleatorio entre 750 y 2000
    let randomNumber = Math.floor(Math.random() * (2000 - 750 + 1)) + 750;

    // Enviar el número como msg.payload
    node.send({ payload: randomNumber });

    // Generar un intervalo aleatorio entre 1 y 5 segundos (1000 a 5000 milisegundos)
    let randomInterval = Math.floor(Math.random() * (8000 - 1000 + 1)) + 1000;

    // Programar la siguiente ejecución
    setTimeout(sendRandomNumber, randomInterval);
}

// Iniciar el envío de números aleatorios
sendRandomNumber();

// No retornar nada, ya que el envío se hace de forma asíncrona
return null;

