// Variables de control de flujo
var start = flow.get("start");      // Variable para iniciar la bomba
var stop = flow.get("stop");        // Variable para detener la bomba
var fallo = flow.get("fallo");      // Estado de fallo de la bomba
var resetFallo = flow.get("resetfallo"); // Resetear el estado de fallo
var bomba = flow.get("bomba");  // Estado actual de la bomba

// Si hay fallo y se recibe resetfallo, se resetea el fallo
if (fallo && resetFallo) {
    fallo = false; // Resetear el fallo
    flow.set("fallo", false); // Guardar estado reseteado
}

// Lógica de control de la bomba
if (fallo) {
    // Si hay fallo, la bomba se apaga
    bomba = false;
}else if (start) {
    // Si se recibe start y no hay fallo, la bomba se enciende
    bomba = true;
}

if (stop) {
    // Si se recibe stop, la bomba se apaga
    bomba = false;
}

// Actualizar el estado de la bomba
flow.set("bomba", bomba);

msg.payload = bomba

return msg;
